// src/pages/Home.js
import React, { useContext } from 'react';
import Header from "../components/Header.js"
import { TaskContext } from '../context/TaskContext.js';
import { Link } from 'react-router-dom';
import AddTask from '../components/AddTask';
import './Home.css';

const Home = () => {
  const  {tasks}  = useContext(TaskContext);

  if (!Array.isArray(tasks)) {
    return <div>Loading tasks...</div>;
  }

  return (

    <>
     <div className="task-board">
      <h2 className="text-center"> List Of All Tasks</h2>
      <div className="task-columns">
        {['To Do', 'In Progress', 'Done'].map((status) => (
          <div key={status} className="task-column">
            <h3>{status}</h3>
            {tasks
              .filter((task) => {
                if (status === 'To Do') return !task.completed;
                if (status === 'Done') return task.completed;
                if (status === 'In Progress') return task.status === 'In Progress';
                return false;
              })
              .map((task , index) => (
                <div key={task.id}>
                  <Link style={{marginBottom:"20px"}} to={`/task/${task.id}`}>T{index+1} : {task.todo}</Link>
                </div>
              ))}
          </div>
        ))}
      </div>
      <AddTask />
    </div>
    </>

  );
};

export default Home;
