// src/pages/Home.js
import React, { useContext } from 'react';
import Header from "../components/Header.js"
import { TaskContext } from '../context/TaskContext';
import { Link } from 'react-router-dom';
import AddTask from '../components/AddTask';

const Home = () => {
  const { tasks } = useContext(TaskContext);
  console.log("tasks")

  if (!tasks) {
    return <p>Loading tasks...</p>; // Add a fallback in case tasks are not yet loaded
  }

  return (
    <>
     <div className="task-board">
      <h1>Task Board</h1>
      <AddTask /> {/* Add Task form */}
      <div className="task-columns">
        {['To Do', 'In Progress', 'Done'].map((status) => (
          <div key={status} className="task-column">
            <h2>{status}</h2>
            {tasks
              .filter((task) => {
                if (status === 'To Do') return !task.completed;
                if (status === 'Done') return task.completed;
                if (status === 'In Progress') return task.status === 'In Progress';
                return false;
              })
              .map((task) => (
                <div key={task.id}>
                  <Link to={`/task/${task.id}`}>{task.todo}</Link>
                </div>
              ))}
          </div>
        ))}
      </div>
    </div>
    </>

  );
};

export default Home;
