import React, { useState, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { TaskContext } from '../context/TaskContext';
import './TaskPage.css';

const TaskPage = () => {
  const { id } = useParams();
  const { tasks, editTask, deleteTask } = useContext(TaskContext);
  const navigate = useNavigate();

  const task = tasks.find((t) => t.id === parseInt(id));
  console.log("task",task)

  const [editMode, setEditMode] = useState(false);
  const [taskText, setTaskText] = useState(task?.todo || '');
  const [taskStatus, setTaskStatus] = useState(task?.status ||" "); // New state for task status

  // Handle task status change
  const handleStatusChange = (newStatus) => {
    setTaskStatus(newStatus);
    // Update the task with the new status locally
    editTask({ id: task.id, todo: taskText, completed: newStatus === 'Done', status: newStatus, userId: task.userId });
  };

  const handleEdit = () => {
    // Save the edited task locally
    editTask({ id: task.id, todo: taskText, completed: taskStatus === 'Done', status: taskStatus, userId: task.userId });
    setEditMode(false);
  };

  const handleDelete = () => {
    // Delete the task and navigate to home page
    deleteTask(task.id);
    navigate('/'); // Redirect to home after deletion
  };

  if (!task) return <div>Task not found</div>;

  return (
    <div className="task-container">
      <div className="task-columns-Task">
        <p> <b>Title :</b> {editMode ? 'Edit Task' : task.todo}</p>
        {editMode ? (
          <div>
            <input
              type="text"
              value={taskText}
              onChange={(e) => setTaskText(e.target.value)}
            />
            <button onClick={handleEdit}>Save</button>
          </div>
        ) : (
          <>
            <p> <b>Status: </b>{taskStatus}</p>
            {/* Dropdown to move between statuses */}
            <select className='w-25' value={taskStatus} onChange={(e) => handleStatusChange(e.target.value)}>
              <option value="To Do">To Do</option>
              <option value="In Progress">In Progress</option>
              <option value="Done">Done</option>
            </select>
          </>
        )}
        <div className='task-buttons'>
          <button type="button" class="btn btn-primary  w-25 text-center" onClick={() => setEditMode(!editMode)}>
            {editMode ? 'Cancel' : 'Edit Task'}
          </button>
          <button type="button" class="btn btn-danger w-25 text-center" onClick={handleDelete}>Delete Task</button>
        </div>

      </div>
    </div>
  );
};

export default TaskPage;
