import React from "react";
import { useState } from "react";



const TaskManagement = () => {

    const [inputTitle, setinputTitle] = useState("");
    const [inputDesc, setinputDesc] = useState("");
    const [items, setitems] = useState([
        {
            id: "001",
            title: "Default Task",
            desc: "Default Description",
            status: false,
        },
    ]);

    const handleInput = (e) => {
        console.log(e.target.value)
        setinputTitle(e.target.value);
    };
    const handleInputdesc = (e) => {
        setinputDesc(e.target.value);
    };

    //ADD FUNCTIONALITY
    const handleAdd = () => {

        console.log("ddhdheru")

    }
    const handleSubmit = (e) => {
        console.log("check");
        //prevent the default behavior of refreshing the page after a submit button.
        e.preventDefault();
        if (!inputTitle || !inputDesc) {
            alert("fill data");
        }
        else {
            const allInputData = {
                id: new Date().getTime().toString(),
                title: inputTitle,
                desc: inputDesc,
            };
            setitems([allInputData, ...items]);

        }
    }

    //DELETE FUNCTIONALITY
    const handleDelete = (index) => {
        console.log(index);
        const updatedItems = items.filter((elem) => {
            return index !== elem.id;
        });
        setitems(updatedItems);
    };

    return (
        <>
            <div className="container">
                <div className="col-12 text-end">
                    <button className="btn btn-primary " onClick={handleAdd}>
                        Add New Task
                    </button>
                </div>
            </div>
            <div className="container border rounded d-flex justify-content-center shadow p-3 mb-5 bg-white rounded">
                <div className="row">
                    <div className="text-center">
                        <h2></h2>
                    </div>
                    <form className="col-12 p-2" >
                        <label htmlFor="title" className="my-2">
                            Enter Title
                        </label>
                        <input
                            type="text"
                            name="title"
                            id="title"
                            placeholder="title"
                            className="w-100 my-1 p-2"
                            onChange={handleInput}
                            value={inputTitle}


                        />
                        <label className="my-2" htmlFor="description">
                            Enter
                        </label>
                        <input
                            type="text"
                            name="description"
                            id="description"
                            placeholder="Description"
                            className="w-100 my-1 p-2"
                            onChange={handleInputdesc}
                            value={inputDesc}
                        />
                        <button className="btn btn-primary my-2" onClick={handleSubmit}>Save</button>
                    </form>
                </div>
            </div>

            <div className="container py-2 ">
                {items.map((elem, index) => {
                    return (
                        <div className="row border rounded shadow p-3 mb-5 bg-white rounded my-3 p-2" key={elem.id}>
                            <div className="col-12 d-flex justify-content-between">
                                <div>
                                    <h4>{elem.name}</h4>
                                    <p>{elem.desc}</p>
                                    {/* <h6>description</h6> */}
                                </div>
                                <div className="d-flex align-items-center">
                                    {/* <button
                                        className="btn btn-primary mx-2"
                                        onClick={() => handleEdit(elem.id)}
                                    >
                                        Edit
                                    </button> */}

                                    {showDelete ? (
                                        <button
                                            className="btn btn-danger mx-2"
                                            onClick={() => handleDelete(elem.id)}
                                        >
                                            Delete
                                        </button>
                                    ) : (
                                        ""
                                    )}
                                </div>
                            </div>
                        </div>
                    )

                })}
                </div>

        </>
            )

}

            export default TaskManagement;
