// src/pages/TaskPage.js
import React, { useState, useContext } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { TaskContext } from '../context/TaskContext';

const TaskPage = () => {
  const { id } = useParams();
  const { tasks, editTask, deleteTask } = useContext(TaskContext);
  const history = useHistory();

  const task = tasks.find((t) => t.id === parseInt(id));

  const [editMode, setEditMode] = useState(false);
  const [taskText, setTaskText] = useState(task?.todo || '');

  const handleEdit = () => {
    editTask({ id: task.id, todo: taskText, completed: task.completed, userId: task.userId });
    setEditMode(false);
  };

  const handleDelete = () => {
    deleteTask(task.id);
    history.push('/'); // Redirect to home after deletion
  };

  if (!task) return <div>Task not found</div>;

  return (
    <div>
      <h1>{editMode ? 'Edit Task' : task.todo}</h1>
      {editMode ? (
        <div>
          <input
            type="text"
            value={taskText}
            onChange={(e) => setTaskText(e.target.value)}
          />
          <button onClick={handleEdit}>Save</button>
        </div>
      ) : (
        <p>Status: {task.completed ? 'Done' : 'To Do'}</p>
      )}
      <button onClick={() => setEditMode(!editMode)}>
        {editMode ? 'Cancel' : 'Edit Task'}
      </button>
      <button onClick={handleDelete}>Delete Task</button>
    </div>
  );
};

export default TaskPage;
