import React from "react";
import axios from 'axios';
import { useState, useEffect } from "react";

const TaskManagement = () => {
    const [tasks, setTasks] = useState([]);
    const [showForm, setshowform] = useState(true);
    const [showNew, setshowNew] = useState(true);
    const [showDelete, setshowDelete] = useState(true);
    const [toggleSubmit, settoggleSubmit] = useState(true);
    const [isEditItem, setisEditItem] = useState(null);
    const [showList, setshowList] = useState(true);
    const [editMessage, seteditMessage] = useState(false);
    const [deleteMessage, setdeleteMessage] = useState(false);
    const [deleteMessagesuccess, setdeleteMessagesuccess] = useState(false);
    const [inputTitle, setinputTitle] = useState("");
    const [inputDesc, setinputDesc] = useState("");
    const [items, setitems] = useState([
        {
            id: "001",
            name: "Default Task",
            desc: "Default Description",
            status: false,
        },
    ]);

    const getTasks = async () => {
        try {
            const res = await axios.get('https://dummyjson.com/users');
            const data = await res.json();
            setTasks((prev) => [...prev, ...data]);

        } catch (error) {
            console.log(error)
        }

    };


    //   HANDLING INPUT FIELDS
    const handleInput = (e) => {
        setinputTitle(e.target.value);
    };
    const handleInputdesc = (e) => {
        setinputDesc(e.target.value);
    };
    //   HANDLING INPUT FIELDS

    //   SUBMITTING FORM
    const handleSubmit = (e) => {
        setshowList(true);
        setshowNew(true);

        e.preventDefault();
        if (!inputTitle || !inputDesc) {
            alert("fill data");
            showList(false);
        } else if (inputTitle && !toggleSubmit) {
            setitems(
                items.map((elem) => {
                    if (elem.id === isEditItem) {
                        return { ...elem, name: inputTitle, desc: inputDesc };
                    }
                    return elem;
                })
            );

            setinputTitle("");
            setinputDesc("");
            settoggleSubmit(true);
            setshowform(false);
            setshowDelete(true);
        } else {
            const allinputTitle = {
                id: new Date().getTime().toString(),
                name: inputTitle,
                desc: inputDesc,
            };
            setitems([allinputTitle, ...items]);
            setinputTitle("");
            setinputDesc("");
            setshowform(false);
        }
    };
    //   SUBMITTING FORM

    //   DELETE
    const handleDelete = (index) => {
        console.log(index);
        const updatedItems = items.filter((elem) => {
            return index !== elem.id;
        });
        setdeleteMessage(true);

        setTimeout(() => {
            setitems(updatedItems);
            setdeleteMessage(false);
        }, 2000);
        setdeleteMessagesuccess(false);
    };
    //   DELETE

    //   EDIT
    const handleEdit = (id) => {
        setshowList(false);
        setshowDelete(false);
        setshowNew(false);
        setshowform(true);

        settoggleSubmit(false);
        let newEditItem = items.find((elem) => {
            return elem.id === id;
        });
        setinputTitle(newEditItem.name);
        setinputDesc(newEditItem.desc);
        // setshowDelete(true)

        setisEditItem(id);
        console.log(newEditItem);
    };
    //   EDIT

    // ADD NEW TASK
    const handleAdd = () => {
        //   alert("hello")
        setshowform(true);
        setshowList(true);
        setshowNew(false);
    };
    useEffect(() => {
        getTasks();

    }, []);
    // ADD NEW TASK
    return (



        <>



            <h1 className='text-center my-9 text-5xl'>All Tasks {tasks.length}</h1>
            <div className='w-full flex flex-row justify-center p-5 gap-10 flex-wrap mt-5'>
                {tasks.length > 0 ? tasks?.map(task => <TaskCard key={task._id} id={task._id} title={task.title} description={task.description} />) : (
                    <div className='flex flex-col items-center justify-center h-[88vh]'>
                        <h1 className='bg-blue-900 text-white p-4 rounded-lg'>No Task To View</h1>
                        <p className='m-2'>Click on Add Task to post a task</p>
                    </div>
                )}
            </div>
            {showNew ? (
                <div className="container">
                    <div className="col-12 text-end">
                        <button className="btn btn-primary " onClick={handleAdd}>
                            New
                        </button>
                    </div>
                </div>
            ) : (
                ""
            )}

            {showForm ? (
                <>
                    <div className="container border rounded d-flex justify-content-center shadow p-3 mb-5 bg-white rounded">
                        <div className="row">
                            <div className="text-center">
                                <h2>{toggleSubmit ? "Add Task" : " Edit Task"}</h2>
                            </div>
                            <form className="col-12 p-2" onSubmit={handleSubmit}>
                                <label htmlFor="title" className="my-2">
                                    Enter Title
                                </label>
                                <input
                                    type="text"
                                    name="title"
                                    id="title"
                                    placeholder="title"
                                    className="w-100 my-1 p-2"
                                    onChange={handleInput}
                                    value={inputTitle}
                                />
                                <label className="my-2" htmlFor="description">
                                    Enter
                                </label>
                                <input
                                    type="text"
                                    name="description"
                                    id="description"
                                    placeholder="Description"
                                    className="w-100 my-1 p-2"
                                    onChange={handleInputdesc}
                                    value={inputDesc}
                                />
                                {/* <div className="text-center"> */}
                                {toggleSubmit ? (
                                    <button className="btn btn-primary my-2">Save</button>
                                ) : (
                                    <button className="btn btn-primary my-2">Update</button>
                                )}
                                {/* </div> */}
                            </form>
                        </div>
                    </div>
                </>
            ) : (
                ""
            )}

            {showList ? (
                <div className="container py-2 ">
                    {deleteMessage ? (
                        <p className="text-center text-danger">Item Deleted Successfully</p>
                    ) : (
                        ""
                    )}
                    {items.map((elem, index) => {
                        return (
                            <div
                                className="row border rounded shadow p-3 mb-3 bg-white rounded  p-2"
                                key={elem.id}
                            >
                                <div className="col-12 d-flex justify-content-between align-items-center">
                                    <div>
                                        <h4>{elem.name}</h4>
                                        <p>{elem.desc}</p>
                                    </div>
                                    <button
                                        className="btn btn-primary mx-2"
                                        onClick={() => handleEdit(elem.id)}
                                    >
                                        Edit
                                    </button>
                                    {showDelete ? (
                                        <button
                                            className="btn btn-danger mx-2"
                                            onClick={() => handleDelete(elem.id)}
                                        >
                                            Delete
                                        </button>
                                    ) : (
                                        ""
                                    )}
                                </div>
                            </div>

                        );
                    })}
                </div>
            ) : (
                ""
            )}
        </>
    );
};


export default TaskManagement;
